ATTACHMENT.Base = "att_pistolgrip" ATTACHMENT.Name = "Ivanov ST-70 Grip" ATTACHMENT.Model = Model("models/zeron/att_vm_p04_ar_akilo_gripstdy_v0.mdl") ATTACHMENT.Icon = Material("zeron/weapons/vepr/icons/icon_attachment_p04_ar_akilo_gripstdy_v0.vmt") local BaseClass = GetAttachmentBaseClass(ATTACHMENT.Base) function ATTACHMENT:Stats(weapon) BaseClass.Stats(self, weapon) weapon.Recoil.Vertical[1] = weapon.Recoil.Vertical[1] * 0.95 weapon.Recoil.Vertical[2] = weapon.Recoil.Vertical[2] * 0.95 weapon.Recoil.Horizontal[1] = weapon.Recoil.Horizontal[1] * 1.05 weapon.Recoil.Horizontal[2] = weapon.Recoil.Horizontal[2] * 1.05 end function ATTACHMENT:PostProcess(weapon) BaseClass.PostProcess(self, weapon) end 